use Web3\Contract;

class CalculatePriceJob
{
    use Dispatchable, InteractsWithQueue, Queueable, SerializesModels;

    /**
     * @var Token
     */
    protected $token;

    /**
     * @param Token $token
     */
    public function __construct(Token $token)
    {
        $this->token = $token;
    }

    /**
     * Execute the job.
     *
     * @return void
     */
    public function handle()
    {
        $result = $this->getAmountsOut(1000000000000000000, $this->token->contract_address, '0xc778417e063141139fce010982780140aa0cd5ab');
        $ethPriceInUsd = $this->getEthPriceInUsd();

        $price = ($result / 10 ** 18) * $ethPriceInUsd;

        $this->token->update([
            'price' => strval($price)
        ]);
    }

