    private function getAmountsOut($amount, $from, $to, $retry = 3) {

        $ex = null;

        while ($retry-- > 0) {

            try {
                $result = $this->callContract($amount, $from, $to);

                foreach ($result as $res) {
                    return intval(strval($res[1]->value));
                }
            }catch (\Exception $e) {
                $ex = $e;
            }
        }

        throw $ex;
    }

    private function getEthPriceInUsd() {
        return Cache::remember('eth_price_in_usd', 1200, function () {
            $json = (new Client())->get('https://api.coingecko.com/api/v3/coins/ethereum')->getBody()->getContents();

            $decode = json_decode($json, true);

            return Arr::get($decode, 'market_data.current_price.usd');
        });
    }
